import Navbar from "../components/Navbar";
import { useAuth } from "../components/auth";
import { useState } from "react";
import "../css/cardData.css";

function CardData({ onClose }) {
    const [formData, setFormData] = useState({
        rut: "",
        correo: "",
        telefono: ""
    });

    const [error, setError] = useState("");

    const verifyRut = (rut) => {
        const dv = rut.slice(-1);
        const body = rut.slice(0, -2);

        try {
            let aux = 2;
            let sum = 0;
            for (let i = body.length - 1; i >= 0; i--) {
                sum += parseInt(body[i]) * aux;

                if (aux === 7) {
                    aux = 2;
                } else {
                    aux++;
                }
            }

            aux = sum % 11;

            switch (aux) {
                case 10:
                    aux = "K";
                    break;
                case 11:
                    aux = "0";
                    break;
                default:
                    aux.toString();
            }

            if (aux !== dv) {
                throw Error("dv no coincide");
            }

            return false
        } catch (err) {
            setError("RUT invalido, ingrese un RUT válido");
            return true
        }
    }

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    return (
        <div className="background" onClick={onClose}>
            <div className="card">
                <h3>Ingresa tus datos personales</h3>
                <form>
                    <div>
                        <label htmlFor="rut">Rut:</label>
                        <input
                            type="text"
                            id="rut"
                            name="rut"
                            value={formData.rut}
                            onChange={handleChange}
                            maxLength={10}
                            placeholder="12345678-K"
                            required
                        />
                    </div>
                    <div>
                        <label htmlFor="correo">Correo Electrónico:</label>
                        <input
                            type="email"
                            id="correo"
                            name="correo"
                            value={formData.correo}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <label htmlFor="telefono">Número de teléfono:</label>
                        <input
                            type="text"
                            id="telefono"
                            name="telefono"
                            value={formData.telefono}
                            onChange={handleChange}
                            required
                        />
                    </div>
                </form>
                <button onClick={onClose}>Cerrar</button>
            </div>
        </div>
    );
}

function Simulator() {
    const { user, isLoading } = useAuth();
    const [isVisible, setIsVisible] = useState(false);

    const toggleVisibility = () => {
        setIsVisible(!isVisible);
    };

    if (isLoading) {
        return null;
    }

    return (
        <div>
            <Navbar />
            {user ? (
                <></>
            ) : (
                <>
                    <h3>Antes de comenzar</h3>
                    <a href="/login">Soy cliente</a>
                    <button onClick={toggleVisibility}>No soy cliente</button>
                    {isVisible && <CardData onClose={toggleVisibility} />}
                </>
            )}
        </div>
    );
}

export default Simulator;
